export async function fill_logged_duel_users(lang) {

    const duel_host_avatar = document.getElementById("duel_host_avatar");
    const duel_host_username = document.getElementById("duel_host_username");

    const duel_host_data = await fetch_user_data();

    if (duel_host_data === null) {
        duel_host_avatar.src = `data:image/jpeg;base64,`;
        duel_host_username.innerHTML = `${lang.anonymous}`;
    }
    else {
        duel_host_avatar.src = `data:image/png;base64,${duel_host_data.profile_pic}`;
        duel_host_username.innerHTML = duel_host_data.username;
    }


    const duel_guest = document.getElementById("duel_guest");
    const duel_guest_avatar = document.getElementById("duel_guest_avatar");
    const duel_guest_username = document.getElementById("duel_guest_username");

    if (duel_guest_avatar === null || duel_guest_username === null) {
        duel_guest.innerHTML = `
            <div class="modal-body p-5 pt-0">
                <form id="form_duel_guest_login">
                    <div class="form-floating mb-3">
                        <input type="text" class="form-control rounded-3" id="duel_guest_username" maxlength="12" required>
                        <label for="duel_guest_username">${lang.username}</label>
                    </div>
                    <div class="form-floating mb-3">
                        <input type="password" class="form-control rounded-3" id="duel_guest_password" maxlength="32" pattern="[a-zA-Z0-9]{6,}" title="${lang.password_format}" required>
                        <label for="duel_guest_password">${lang.password}</label>
                    </div>
                    <div id="info_duel_guest"></div>
                    <button class="w-100 mb-2 btn btn-lg rounded-3 btn-primary" type="submit" id="duel_guest_submit">${lang.login}</button>
                </form>
                <p class="mt-4 mb-2 text-muted text-end">${lang.guest}</p>
            </div>`
    }
    else {
        const duel_guest_data = await fetch_guest_data('duel', 2);

        if (duel_guest_data === null) {
            duel_guest_avatar.src = `data:image/jpeg;base64,`;
            duel_guest_username.innerHTML = `${lang.anonymous}`;
        }
        else {
            duel_guest_avatar.src = `data:image/png;base64,${duel_guest_data.profile_pic}`;
            duel_guest_username.innerHTML = duel_guest_data.username;
        }
    }

    if (duel_guest_avatar !== null) {
        const start_button = document.getElementById("start_button_location");

        start_button.innerHTML = `<button type="button" class="btn btn-lg btn-success ms-1 w-100" type="button" id="duel_start">${lang.start} !</button>`;
    }
}

