export async function fill_game_users(lang) {
    const modelsNames = Object.keys(modelsPath);

    const carousel1 = document.getElementById("carousel1");
    const carousel2 = document.getElementById("carousel2");

    modelsNames.forEach((item, index) => {
        //create carousel items
        const carousel_item = document.createElement('div');
        carousel_item.classList.add('carousel-item');
        if (index === 0) carousel_item.classList.add('active');

        const img = document.createElement('img');
        img.classList.add('d-block');
        img.id = "carousel-img1";
        img.alt = item;
        img.src = `ressources/game/Characters/PNG/${item}.png`;

        carousel_item.appendChild(img);
        carousel1.appendChild(carousel_item);
    });

    modelsNames.forEach((item, index) => {
        //create carousel items
        const carousel_item = document.createElement('div');
        carousel_item.classList.add('carousel-item');
        if (index === 0) carousel_item.classList.add('active');

        const img = document.createElement('img');
        img.classList.add('d-block');
        img.id = "carousel-img2";
        img.alt = item;
        img.src = `ressources/game/Characters/PNG/${item}.png`;

        carousel_item.appendChild(img);
        carousel2.appendChild(carousel_item);
    });

    const duel_host = document.getElementById("profile_avatar_left");

    const duel_host_data = await fetch_user_data();

    if (duel_host_data === null) {
        duel_host.src = `data:image/jpeg;base64,`;
    }
    else {
        duel_host.src = `data:image/png;base64,${duel_host_data.profile_pic}`;
    }


    const duel_guest = document.getElementById("profile_avatar_right");

        const duel_guest_data = fetch_guest_data('duel', 2);

        if (duel_guest_data === null) {
            duel_guest.src = `data:image/jpeg;base64,`;
            //duel_guest_username.innerHTML = `${lang.anonymous}`;
        }
        else {
            duel_guest.src = `data:image/png;base64,${duel_guest_data.profile_pic}`;
            //duel_guest_username.innerHTML = duel_guest_data.username;
        }
}

export async function fill_tournament_order_players(lang) {
    const player1_avatar = document.getElementById("player1_avatar");
    const player1_username = document.getElementById("player1_username");

    const player1_data = await fetch_user_data();

    if (player1_data === null) {
        player1_avatar.src = `data:image/jpeg;base64,`;
        player1_username.innerHTML = `${lang.anonymous}`;
    }
    else {
        player1_avatar.src = `data:image/png;base64,${player1_data.profile_pic}`;
        player1_username.innerHTML = player1_data.username;
    }

    const player2_avatar = document.getElementById("player2_avatar");
    const player2_username = document.getElementById("player2_username");

    const player2_data = await fetch_guest_data('tournament', 2);

    if (player2_data === null) {
        player2_avatar.src = `data:image/jpeg;base64,`;
        player2_username.innerHTML = `${lang.anonymous}`;
    }
    else {
        player2_avatar.src = `data:image/png;base64,${player2_data.profile_pic}`;
        player2_username.innerHTML = player2_data.username;
    }

    const player3_avatar = document.getElementById("player3_avatar");
    const player3_username = document.getElementById("player3_username");

    const player3_data = await fetch_guest_data('tournament', 3);

    if (player3_data === null) {
        player3_avatar.src = `data:image/jpeg;base64,`;
        player3_username.innerHTML = `${lang.anonymous}`;
    }
    else {
        player3_avatar.src = `data:image/png;base64,${player3_data.profile_pic}`;
        player3_username.innerHTML = player3_data.username;
    }

    const player4_avatar = document.getElementById("player4_avatar");
    const player4_username = document.getElementById("player4_username");

    const player4_data = await fetch_guest_data('tournament', 4);

    if (player4_data === null) {
        player4_avatar.src = `data:image/jpeg;base64,`;
        player4_username.innerHTML = `${lang.anonymous}`;
    }
    else {
        player4_avatar.src = `data:image/png;base64,${player4_data.profile_pic}`;
        player4_username.innerHTML = player4_data.username;
    }

    const next_match_number = await fetch_next_match();

    var next_match;

    if (next_match_number === null || next_match_number === 1) {
        next_match = document.getElementById("game1");
    }
    else if (next_match_number === 2) {
        next_match = document.getElementById("game2");
    }
    else if (next_match_number === 3) {
        next_match = document.getElementById("game3");
    }
    else {
        next_match = document.getElementById("game4");
    }

    next_match.classList.add('next_match');

    const game1_p1 = document.getElementById("game1_p1");
    game1_p1.innerHTML = player1_username.innerHTML + `<span class="score" id="game1_p1_score">-</span>`;

    const game1_p2 = document.getElementById("game1_p2");
    game1_p2.innerHTML = player2_username.innerHTML  + `<span class="score" id="game1_p2_score">-</span>`;

    const game1_score = await fetch_game_score(1);

    const game1_p1_score = document.getElementById("game1_p1_score");
    const game1_p2_score = document.getElementById("game1_p2_score");

    if (game1_score !== null) {
        game1_p1_score = `${game1_score.p1}`;
        game1_p2_score = `${game1_score.p2}`;
    }

    const game2_p1 = document.getElementById("game2_p1");
    game2_p1.innerHTML = player3_username.innerHTML + `<span class="score" id="game2_p1_score">-</span>`;

    const game2_p2 = document.getElementById("game2_p2");
    game2_p2.innerHTML = player4_username.innerHTML + `<span class="score" id="game2_p2_score">-</span>`;

    const game2_score = await fetch_game_score(2);

    const game2_p1_score = document.getElementById("game2_p1_score");
    const game2_p2_score = document.getElementById("game2_p2_score");

    if (game2_score !== null) {
        game2_p1_score = `${game2_score.p1}`;
        game2_p2_score = `${game2_score.p2}`;
    }

    const game3_p1 = document.getElementById("game3_p1");
    const game3_p2 = document.getElementById("game3_p2");
    const game4_p1 = document.getElementById("game4_p1");
    const game4_p2 = document.getElementById("game4_p2");

    // If the first match is done, start filling the second round players
    if (next_match >= 2 && game1_score !== null) {
        if (game1_score.p1 > game1_score.p2) {
            game4_p1.innerHTML = player1_username.innerHTML;
            game3_p1.innerHTML = player2_username.innerHTML;
        }
        else {
            game4_p1.innerHTML = player2_username.innerHTML;
            game3_p1.innerHTML = player1_username.innerHTML;
        }
    }
    // If the second match is also done, finish filling the tree
    if (next_match >= 3 && game2_score !== null) {

        if (game2_score.p1 > game2_score.p2) {
            game4_p2.innerHTML = player3_username.innerHTML;
            game3_p2.innerHTML = player4_username.innerHTML;
        }
        else {
            game4_p2.innerHTML = player4_username.innerHTML;
            game3_p2.innerHTML = player3_username.innerHTML;
        }
    }

    const game3_score = await fetch_game_score(3);

    const game3_p1_score = document.getElementById("game3_p1_score");
    const game3_p2_score = document.getElementById("game3_p2_score");

    if (game3_score !== null) {
        game3_p1_score = `${game3_score.p1}`;
        game3_p2_score = `${game3_score.p2}`;
    }

    const game4_score = await fetch_game_score(4);

    const game4_p1_score = document.getElementById("game4_p1_score");
    const game4_p2_score = document.getElementById("game4_p2_score");

    if (game4_score !== null) {
        game4_p1_score = `${game4_score.p1}`;
        game4_p2_score = `${game4_score.p2}`;

        const button = document.getElementById("next_match_input");
        button.innerHTML = `${lang.home}`;
        button.id = "profile_button";

        const tree = document.getElementById("tree");
        tree.innerHTML = `${lang.result}:`;
    }
}
